package datastructure;

public class AmazingSubstrings {
	  public int solve(String A) {
	        
	        String vowel="aeiouAEIOU";
	        int n=A.length();
	        int count=0;
	        for(int i=0;i<n;i++){
	            char c =A.charAt(i);
	            if(vowel.indexOf(c)!=-1){
	              count+= n-i;
	            }
	        }
	       
	        return count%10003;
	       // return count;
	    }
	  public static void main(String[] args){
		  AmazingSubstrings a = new AmazingSubstrings();
		  String s="";
		  System.out.println(a.solve(s));
	  }
}
